    '#FFDBAC': { bellyColor: '#FFCC8A', handsFeetColor: '#FFCC8A' },

    // Tan family - fix
    '#E0AC69': { bellyColor: '#D48B2B', handsFeetColor: '#D48B2B' },

    // Brown family
