    // Dark Brown family
    '#8D5524': { bellyColor: '#5D3818', handsFeetColor: '#5D3818' },

    // Espresso family
    '#5C3A21': { bellyColor: '#342113', handsFeetColor: '#342113' },

    // Orange family - fix
    '#FFB347': { bellyColor: '#FF9500', handsFeetColor: '#FF9500' },

    // Yellow family - fix
